package topics;

public class Print_Even_Number {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int i=10;
		
		while(i<=20)
		{
			System.out.println(i);
			i=i+2;
		}
		
		
		
		
		System.out.println("------------------");
		
		
		int j=20;
		
		do
		{
			System.out.println(j);
			j=j+2;
			
		}while(j<=30);
		
		
		
		
		
		System.out.println("---------------------");
		
		
		for(int k=2;k<=10; k=k+2)
			
		{
			
			System.out.println(k);
			
		}
		
	}

}
