package topics;

public class Swap_two_Numbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int x=5,y=10,z;
		
		System.out.println("Before swapping \n x=" +x + "\n y=" +y);
		
		z=x; x=y; y=z;
	
		System.out.println("After swapping \n x=" + x +"\n y=" +y);
		
	}

}