package topics;

public class do_While_Loop_6 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		int i=0;
		
		do
			
		{
			
			System.out.println("Value of I : " + i);
			i++;
			
		}while(i<=10);
		
		
		System.out.println("Do while loop exit because Value of I is : " + i);
		
		
		
	

	}

}
